// src/pages/UploadPage.js
import React, { useState } from 'react';
import './UploadPage.css'; // Import the CSS file for styling

function UploadPage() {
  const [content, setContent] = useState(null);
  const [preview, setPreview] = useState(''); // Store the file preview (text or name)
  const [message, setMessage] = useState(''); // Store any messages (success/error)

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setContent(file);

    if (file) {
      if (file.type === 'text/plain' || file.type === 'application/pdf') {
        // For text-based files, read the content to preview
        const reader = new FileReader();
        reader.onload = () => {
          setPreview(reader.result.substring(0, 500)); // Limit preview to first 500 characters
        };
        reader.readAsText(file);
      } else {
        // For other files, just show the name
        setPreview(`Selected file: ${file.name}`);
      }
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('file', content);

    try {
      const response = await fetch('http://localhost:5000/api/upload', { // Ensure this points to your Flask server
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (response.ok) {
        setMessage('File uploaded successfully!');
        setPreview(data.preview); // Preview file content from server if available
      } else {
        setMessage(`Error: ${data.error}`);
      }
    } catch (error) {
      setMessage('Upload failed. Please try again.');
      console.error('Upload failed', error);
    }
  };

  return (
    <div className="upload-container">
      <div className="upload-card">
        <h2>Upload Content</h2>
        <form onSubmit={handleUpload}>
          {/* Custom-styled button for file selection */}
          <label htmlFor="file-upload" className="custom-file-upload">
            Choose File
          </label>

          {/* Hidden file input */}
          <input
            id="file-upload"
            type="file"
            style={{ display: 'none' }} // Hide the default file input
            onChange={handleFileChange}
            required
          />

          <button type="submit">Upload</button>
        </form>

        {/* Display message */}
        {message && <p>{message}</p>}

        {/* Display file preview */}
        {preview && (
          <div>
            <h3>File Preview:</h3>
            <p>{preview}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default UploadPage;




